def findMarkerPos(str):
	for idx,x in enumerate(str):
		if(idx >= 3):
			lastBit = str[idx-3:idx+1]
			#print('idx',idx,'lb',lastBit)
			isUnique = True
			for char in lastBit:
				#print('char', char, 'count', lastBit.count(char))
				if lastBit.count(char) > 1:

					isUnique = False
				
			if isUnique:
				print('last bit', lastBit)
				return idx+1


